// pc or mobile（true:是，false:不是移动端）
const isMobile = () => {
  let ua = navigator.userAgent;
  let Agents = ['iOS','Android', 'iPhone', 'SymbianOS', 'Windows Phone', 'BlackBerry', 'iPad', 'iPod'];
  let getArr =  Agents.filter(i => ua.includes(i));
  return getArr.length ? true : false;
}

// load css file
// @param {String} u 文件路径
// @param {String} t 文件类型标签（<link>）
const loadCss = (u, t) => {
  const s = document.createElement(t);
  const fa = document.querySelector('title');
  if (t === "link") {
    s.href = u;
    s.rel = "stylesheet";
    s.type = "text/css";
    s.characterSet;
  }
  document.head.insertBefore(s, fa);
}

// create elements and add classnames and id names
// @param {string} eleTag 元素标签
// @param {string} className 元素类名
// @param {string} id 元素id
const createEleAddClassAndId = (eleTag ,className, id) => {
  let e = document.createElement(eleTag);
  e.setAttribute("class", className);
  e.setAttribute("id", id);
  return e;
}

// append elements to body
const addNode = (e) => {
  document.body.appendChild(e);
}

// get languages
const getLang = () => {
  let lang = document.getElementsByTagName('html')[0].getAttribute('lang') || document.documentElement.lang;
  return lang;
}

// set languages
// @param {String} newLang 语言名称
const setLang = (newLang) => {
  let lang = document.getElementsByTagName('html')[0].setAttribute('lang', newLang) || document.documentElement.setAttribute('lang', newLang);;
  return lang;
}

// theme change
// @param {String} themeName 主题/皮肤名称
const changeTheme = (themeName) => {
  document.documentElement.setAttribute("data-theme", themeName);
}

// 插入<style>标签样式到<title>标签前
// @param {String} eleStyleInit 样式表内容
const addStyle = (eleStyleInit) => {
  const fa = document.querySelector('title');
  const eleStyle = document.createElement('style');
  eleStyle.innerHTML = eleStyleInit;
  document.head.insertBefore(eleStyle, fa);
}

// add Inline style
// @param {String} ele 需要添加行内样式的元素id或class
// @param {String | Map} eleCss 具体的样式（多个用分号分隔）
const addInlineStyle = (ele, eleCss) => {
  let e = document.getElementById(ele) || document.getElementsByClassName(ele);
  e.style.cssText = eleCss;
}

// show elements
const showEle = (ele) => {
  document.getElementById(ele).style.display = 'block';
}

// hidden elements
const hiddenEle = (ele) => {
  document.getElementById(ele).style.display = 'none';
}

